import React, { useEffect, useState } from "react";
import { Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { getAllMoives } from "../redux/actions/moives";
import CardMovie from "./CardMovie";
import AdvancedExample from "./Pagination"
const MoviesList = ({ getPage,pageCount }) => {

  const [movies, setMovies] = useState([]);
const dispatch=useDispatch();
 useEffect(() => {
   
    dispatch(getAllMoives())
  }, []);
const dataMovies=useSelector((state)=> state.movies)
useEffect(()=>{

  setMovies(dataMovies)

},[dataMovies])
  return (
    <Row className="mt-3">
    {movies.length >= 1 ? (
      movies.map((mov) => {
        return <CardMovie key={mov.id} mov={mov} />;
      })
    ) : (
      <h2 className="text-center p-5">لا يوجد أفلام</h2>
    )}
    <AdvancedExample getPage={getPage} pageCount={pageCount}/>
  </Row>
  );
};

export default MoviesList;
